import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [ruc, setRuc] = useState("");
  const [placa, setPlaca] = useState("");
  const [cedula, setCedula] = useState("");
  const [correo, setCorreo] = useState("");
  const [resultado, setResultado] = useState("");

  const consultarSRI = async () => {
    try {
      const res = await axios.get(`/api/sri/${ruc}`);
      setResultado(JSON.stringify(res.data));
    } catch (err) {
      setResultado("Error consultando SRI");
    }
  };

  const consultarVehiculo = async () => {
    try {
      const res = await axios.get(`/api/vehiculo/${placa}`);
      setResultado(JSON.stringify(res.data));
    } catch (err) {
      setResultado("Error consultando vehículo");
    }
  };

  const consultarLicencia = async () => {
    try {
      const res = await axios.get(`/api/licencia/${cedula}/${placa}`);
      setResultado(JSON.stringify(res.data));
    } catch (err) {
      setResultado("Error consultando licencia");
    }
  };

  return (
    <div>
      <h1>Consulta SRI/ANT</h1>
      <input placeholder="Correo" value={correo} onChange={e => setCorreo(e.target.value)} />
      <br />
      <input placeholder="RUC" value={ruc} onChange={e => setRuc(e.target.value)} />
      <button onClick={consultarSRI}>Consultar SRI</button>
      <br />
      <input placeholder="Placa" value={placa} onChange={e => setPlaca(e.target.value)} />
      <button onClick={consultarVehiculo}>Consultar Vehículo</button>
      <br />
      <input placeholder="Cédula" value={cedula} onChange={e => setCedula(e.target.value)} />
      <button onClick={consultarLicencia}>Consultar Puntos Licencia</button>
      <br />
      <div>Resultado: {resultado}</div>
    </div>
  );
}
