import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [ruc, setRuc] = useState("");
  const [placa, setPlaca] = useState("");
  const [cedula, setCedula] = useState("");
  const [correo, setCorreo] = useState("");

  const [sriData, setSriData] = useState(null);
  const [vehiculoData, setVehiculoData] = useState(null);
  const [licenciaData, setLicenciaData] = useState(null);

  const [sriOk, setSriOk] = useState(false);
  const [vehiculoOk, setVehiculoOk] = useState(false);
  const [error, setError] = useState("");

  // 1️⃣ Consultar SRI
  const consultarSRI = async () => {
    setError("");
    setVehiculoData(null);
    setLicenciaData(null);
    try {
      const res = await axios.get(`/api/sri/${ruc}`);
      if (res.data === "No es contribuyente") {
        setSriData(null);
        setSriOk(false);
        setError("No es contribuyente");
      } else {
        setSriData(res.data);
        setSriOk(true);
      }
    } catch (err) {
      console.error(err);
      setError("Error consultando SRI");
      setSriOk(false);
    }
  };

  // 2️⃣ Consultar Vehículo
  const consultarVehiculo = async () => {
    if (!sriOk) return setError("Primero consulta SRI");
    setLicenciaData(null);
    setError("");
    try {
      const res = await axios.get(`/api/vehiculo/${placa}`);
      setVehiculoData(res.data);
      setVehiculoOk(true);
    } catch (err) {
      console.error(err);
      setError("Error consultando vehículo");
      setVehiculoOk(false);
    }
  };

  // 3️⃣ Consultar Licencia
  const consultarLicencia = async () => {
    if (!vehiculoOk) return setError("Primero consulta vehículo");
    setError("");
    try {
      const res = await axios.get(`/api/licencia/${cedula}/${placa}`);
      setLicenciaData(res.data);
    } catch (err) {
      console.error(err);
      setError("Error consultando licencia");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Consulta SRI / ANT</h1>

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="Correo"
          value={correo}
          onChange={e => setCorreo(e.target.value)}
          style={{ marginRight: "10px" }}
        />
      </div>

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="RUC"
          value={ruc}
          onChange={e => setRuc(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={consultarSRI}>Consultar SRI</button>
      </div>

      {sriData && (
        <div style={{ marginBottom: "20px" }}>
          <h3>Datos del Contribuyente</h3>
          <table border="1" cellPadding="5">
            <tbody>
              {Object.entries(sriData).map(([key, value]) => (
                <tr key={key}>
                  <td><b>{key}</b></td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="Placa"
          value={placa}
          onChange={e => setPlaca(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={consultarVehiculo}>Consultar Vehículo</button>
      </div>

      {vehiculoData && (
        <div style={{ marginBottom: "20px" }}>
          <h3>Datos del Vehículo</h3>
          <table border="1" cellPadding="5">
            <tbody>
              {Object.entries(vehiculoData).map(([key, value]) => (
                <tr key={key}>
                  <td><b>{key}</b></td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="Cédula"
          value={cedula}
          onChange={e => setCedula(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={consultarLicencia}>Consultar Puntos Licencia</button>
      </div>

      {licenciaData && (
        <div style={{ marginBottom: "20px" }}>
          <h3>Puntos de Licencia</h3>
          <table border="1" cellPadding="5">
            <tbody>
              {Object.entries(licenciaData).map(([key, value]) => (
                <tr key={key}>
                  <td><b>{key}</b></td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {error && <div style={{ color: "red" }}>Error: {error}</div>}
    </div>
  );
}
