import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [ruc, setRuc] = useState("");
  const [placa, setPlaca] = useState("");
  const [cedula, setCedula] = useState("");
  const [resultado, setResultado] = useState(null);

  const consultarSRI = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/sri/${ruc}`);
      setResultado(res.data); // res.data es un array de objetos
    } catch (err) {
      setResultado([{ error: "Error consultando SRI" }]);
    }
  };

  const consultarVehiculo = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/vehiculo/${placa}`);
      setResultado(res.data);
    } catch (err) {
      setResultado([{ error: "Error consultando vehículo" }]);
    }
  };

  const consultarLicencia = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/licencia/${cedula}/${placa}`);
      setResultado(res.data);
    } catch (err) {
      setResultado([{ error: "Error consultando licencia" }]);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Consulta SRI/ANT</h1>

      <input
        placeholder="RUC"
        value={ruc}
        onChange={e => setRuc(e.target.value)}
      />
      <button onClick={consultarSRI}>Consultar SRI</button>
      <br /><br />

      <input
        placeholder="Placa"
        value={placa}
        onChange={e => setPlaca(e.target.value)}
      />
      <button onClick={consultarVehiculo}>Consultar Vehículo</button>
      <br /><br />

      <input
        placeholder="Cédula"
        value={cedula}
        onChange={e => setCedula(e.target.value)}
      />
      <button onClick={consultarLicencia}>Consultar Licencia</button>
      <br /><br />

      <div>
        <h2>Resultado:</h2>
        {resultado && resultado.length > 0 ? (
          resultado.map((item, index) => (
            <div key={index} style={{ border: "1px solid #ccc", margin: "10px 0", padding: "10px" }}>
              {Object.entries(item).map(([key, value]) => (
                <div key={key}>
                  <strong>{key}:</strong>{" "}
                  {typeof value === "object" && value !== null
                    ? JSON.stringify(value, null, 2)
                    : value}
                </div>
              ))}
            </div>
          ))
        ) : (
          "No hay datos"
        )}
      </div>
    </div>
  );
}
